// Deep merges properties from 'data' into 'target'
// Expects input to have 'target' and 'data' keys or adjusts validation

function deepMerge(target, source) {
  for (const key of Object.keys(source)) {
    if (source[key] instanceof Object && key in target) {
      Object.assign(source[key], deepMerge(target[key], source[key]));
    }
  }
  Object.assign(target || {}, source);
  return target;
}

const results = [];
for (const item of items) {
  const target = item.json.target || {};
  const source = item.json.source || {};
  results.push({ json: deepMerge(target, source) });
}

return results;
