// Pick specific keys from the JSON object
const keysToPick = '{{ keys | default('id,name') }}'.split(',').map(k => k.trim());

return items.map(item => {
  const newItem = {};
  keysToPick.forEach(key => {
    if (item.json.hasOwnProperty(key)) {
      newItem[key] = item.json[key];
    }
  });
  return { json: newItem };
});
