// Validates that the input item contains required fields
const requiredFields = ['{{ fields | default('id,name') '}.split(',')];
const missing = [];

for (const field of requiredFields) {
  if (!items[0].json.hasOwnProperty(field.trim())) {
    missing.push(field.trim());
  }
}

if (missing.length > 0) {
  throw new Error(`Missing required fields: ${missing.join(', ')}`);
}

return items;
